import React, { Component } from 'react'
import './context.css'

export default class ContextDemo extends Component {
    state = {user:{name:'Billtt',age:38}}

    render() {
        let info = `用户名:${user.name},年龄: ${user.age}`
        return (
            <div className='parent'>
                <h2>我是ContextDemo组件,我传递的信息是{info}</h2>
                <A className='child' user={this.state.user}/>
            </div>
        )
    }
}

class A extends Component {

    render() {
        let {user} = this.props;
        let info = `用户名:${user.name},年龄: ${user.age}`

        return (
            <div>
                <h2>我是A组件,我接收到的信息是：{info}</h2>
                <B className='grand'/>
            </div>
        )
    }
}

class B extends Component {
    render() {
        return (
            <div>
                <h2>我是B组件,我接收到的信息是</h2>
                <C className='grand2'/>
            </div>
        )
    }
}

function C() {
    return (
        <div>
            <h2>我是C组件,我接收到的信息是</h2>
        </div>
    )
}
