import React,{Component} from 'react'

export default class Search extends Component{

    savekeyWord = (event) => {
      let keyWord = event.target.value;
      this.setState({keyWord})      
    }

    searchUsers = () => {
      let {keyWord} = this.state;
      //axios
      let users = [{name:keyWord,href:'https://github.com/reactjs1',imgSrc:'https://avatars.githubusercontent.com/u/6412038?v=3'},
      {name:keyWord,href:'https://github.com/reactjs2',imgSrc:'https://avatars.githubusercontent.com/u/6412038?v=3'}];
      this.props.updateUsers(users);
    }

    render(){
        return (
            <section className="jumbotron">
            <h3 className="jumbotron-heading">Search Github Users</h3>
            <div>
              <input onChange={this.savekeyWord} type="text" placeholder="enter the name you search"/>&nbsp;
              <button onClick={this.searchUsers}>Search</button>
            </div>
          </section>
        )
    }
}
