import React from 'react';
import Search from './components/Search/Search'
import List from './components/List/List'

class App extends React.Component{
  state = {users:[],
           isFirst: true,
           isLoading: false,
           isError: false,
           error: null
  }

  updateUsers = (users) => {
    this.setState({users:users})
  }

  updateIsFirst = (isFirst) => {
    this.setState(...this.state,isFirst)
  }

  updateIsLoading = (isLoading) => {
    this.setState(...this.state,isLoading)
  }

  updateIsError = (isError,error) => {
    this.setState(...this.state,isError,error)
  }

  render(){
    return (
      <div className="container">
        <Search updateIsFirst={this.updateIsFirst} updateIsLoading={this.updateIsLoading} updateIsError={this.updateIsError} updateUsers={this.updateUsers}/>
        <List {...this.state}/>
    </div>
    );
  }
}

export default App;
