import './Item.css'

export default class Item extends Component {

    showDelBtn = () =>{

    }

    hideDelBtn = () => {

    }

    render() {
        let {id,name,done} = this.props;
        return (
            <div onMouseEnter={this.showDelBtn} onMouseLeave={this.hideDelBtn} className='item'>
                <input type='checkbox' defaultChecked={done}></input>
                <span>{name}</span>
                <input className='delBtn' type='button' value='删除'></input>
            </div>
        )
    }
}
