import React, { Component } from 'react'
import './Item.css'

export default class Item extends Component {

    state = {flag:false}

    backgroundColor = (flag) => {
        return () => {
            this.setState({flag:flag})
        }
    }

    render() {
        let {id,name,done} = this.props;
        return (
            <div onMouseEnter={this.backgroundColor(true)} onMouseLeave={this.backgroundColor(false)} className='item' style={{backgroundColor: this.state.backgroundColor?'#ddd':'white'}}>
                <input type='checkbox' defaultChecked={done}></input>
                <span>{name}</span>
                <input className='delBtn' type='button' value='删除'></input>
            </div>
        )
    }
}
