import React from 'react';
import Header from './components/Header/Header';
import List from './components/List/List'
import Footer from './components/Footer/Footer';

class App extends React.Component{

  state = {toDoList:[{id:1,name:'学习',done:true},{id:2,name:'吃饭',done:false},{id:3,name:'睡觉',done:false}]}

  addToDoItem = item => {
    let {toDoList} = this.state;
    toDoList.unshift(item)
    this.setState({toDoList:toDoList})
  }

  changeItemDoneStatus = (id,done) => {
    console.log(id,done)
    let {toDoList} = this.state;
    toDoList.map(item => {
      if(item.id === id){
        item.done = done;
      }

      return item;
    })

    this.setState({toDoList:toDoList})
  }

  render(){
    return (
      <div className="App">
        <Header addToDoItem={this.addToDoItem}/>
        <List changeItemDoneStatus={this.changeItemDoneStatus} toDos={this.state.toDoList}/>
        <Footer toDos={this.state.toDoList}/>
      </div>
    );
  }
}

export default App;
