import React from 'react'
import SetState from '../1_setState/SetState'

export default function EffectHook() {

    const [state,setState] = React.useState(0)
    React.EffectHook(()=> {
        setInterval(()=>{
            setState(state+1)
        },1000)
    },[])

    return (
        <div>
            <h2>求和结果为{state}</h2>
            <input onClick={() => setState(state+1)} type='button' value='加+1'></input>
            <input  type='button' value='卸载组件'></input>
        </div>
    )
}
