import { Button } from 'antd'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import {Card} from 'antd'
import ReactEcharts from 'echarts-for-react'

class GIStatistics extends Component {
    updateChart = () => {}
    getOption = () => {
        var option = {
            title: {
                text: 'ECharts 入门示例'
            },
            tooltip: {},
            legend: {
                data:['销量']
            },
            xAxis: {
                data: ["衬衫","羊毛衫","雪纺衫","裤子","高跟鞋","袜子"]
            },
            yAxis: {},
            series: [{
                name: '销量',
                type: 'bar',
                data: [5, 20, 36, 10, 10, 20]
            }]
        };

        return option;
    }
    render() {
        const title = 'Global International Request Statistics'

        return (
            <div>
                <Card>
                    <Button type='primary' onClick={this.updateChart}>Update</Button>
                </Card>
                <Card title={title}>
                    <ReactEcharts option={this.getOption()} />
                </Card>
            </div>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(GIStatistics)
