import { Button } from 'antd'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import {Card} from 'antd'
import ReactEcharts from 'echarts-for-react'

class GIStatistics extends Component {
    state = {gistatics:[5, 20, 36, 10, 10, 20,5, 20, 36, 10, 10, 20]}

    updateChart = () => {
        let gistatics = this.state.gistatics.map(item => item+1)
        this.setState({gistatics})
    }

    getOption = () => {
        var option = {
            title: {
                text: 'Global-Int Request Total'
            },
            tooltip: {},
            legend: {
                data:['Total']
            },
            xAxis: {
                data: ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"]
            },
            yAxis: {},
            series: [{
                name: '销量',
                type: 'bar',
                data: this.state.gistatics
            }]
        };

        return option;
    }
    render() {
        const title = 'Global International Request Statistics'

        return (
            <div>
                <Card>
                    <Button type='primary' onClick={this.updateChart}>Update</Button>
                </Card>
                <Card title={title}>
                    <ReactEcharts option={this.getOption()} />
                </Card>
            </div>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(GIStatistics)
