import React, { Component } from 'react'
import { connect } from 'react-redux'
import './LeftNav.less'
import Logo from '../../assets/images/logo.png'
import { Link } from 'react-router-dom'
import { Menu } from 'antd';
import {
    AppstoreOutlined,
    PieChartOutlined,
    DesktopOutlined,
    ContainerOutlined,
    MailOutlined,
} from '@ant-design/icons';
import menuList from '../../config/menuListConfig'

const { SubMenu } = Menu;

class LeftNav extends Component {

    state = {
        collapsed: false,
    };

    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    };

    generateMenuList = (menuList) => {
        return menuList.map(menu => {
            if (!menu.children) {
                return (
                    <Menu.Item key={menu.key} icon={<PieChartOutlined />}>
                        <Link to={menu.key}>{menu.title}</Link>
                    </Menu.Item>
                )
            } else {
                return (
                    <SubMenu key={menu.key} icon={<DesktopOutlined />} title={menu.title}>
                        {
                            menu.children.map(child => {
                                return (
                                    <Menu.Item key={child.key} icon={<PieChartOutlined />}>
                                        <Link to={child.key}>{child.title}</Link>
                                    </Menu.Item>
                                )
                            })
                        }
                    </SubMenu>
                )
            }
        })
    }

    render() {
        return (
            <div className='left-nav'>
                <Link to="/dashboard" className='left-nav-header'>
                    <img src={Logo} alt="logo"></img>
                </Link>
                <div style={{ width: 200 }}>
                    <Menu
                        defaultSelectedKeys={['home']}
                        defaultOpenKeys={['sub1']}
                        mode="inline"
                        theme="light"
                        inlineCollapsed={this.state.collapsed}
                    >
                        <Menu.Item key="home" icon={<PieChartOutlined />}>
                            <Link to='/dashboard/home'>Home</Link>
                        </Menu.Item>
                        {/* <Menu.Item key="2" icon={<DesktopOutlined />}>
                            Submit Proxy Forms
                        </Menu.Item>
                        <Menu.Item key="3" icon={<ContainerOutlined />}>
                            Option 3
                        </Menu.Item> */}
                        <SubMenu key="GI" icon={<DesktopOutlined />} title="Mobility GI">
                            <Menu.Item key="GI-Submit">
                                <Link to='/dashboard/gi-submit'>Submit</Link>
                            </Menu.Item>
                            <Menu.Item key="GI-Check">
                                <Link to='/dashboard/gi-check'>Check</Link>
                            </Menu.Item>
                        </SubMenu>
                        <SubMenu key="DO" icon={<AppstoreOutlined />} title="Mobility Domestic">
                            <Menu.Item key="DO-Submit">
                                <Link to='/dashboard/do-submit'>Submit</Link>
                            </Menu.Item>
                            <Menu.Item key="DO-Check">
                                <Link to='/dashboard/do-check'>Check</Link>
                            </Menu.Item>
                            {/* <SubMenu key="sub3" title="Submenu">
                                <Menu.Item key="11">Option 11</Menu.Item>
                                <Menu.Item key="12">Option 12</Menu.Item>
                            </SubMenu> */}
                        </SubMenu>
                    </Menu>
                </div>
            </div>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(LeftNav)
