import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { Button, Card, Table, Tag, Space, message } from 'antd'
import { PlusOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import {getSubmittedRequests} from '../../api'
import {Link} from 'react-router-dom'
import {cachePageNum} from '../../redux/actions/page'

const { Column } = Table;

class GICheck extends Component {
    state = {submittedRequests:[],loading:false}

    fetchSubmittedRequests = async (type) => {
        this.setState({loading:true})
        let response = await getSubmittedRequests(type)
        if(response.code === 0){
            let submittedRequests = response.data
            this.setState({submittedRequests,loading:false})
        }else if(response.code === 405){
            this.props.unCacheUser()
            this.props.history.replace('/launch')   
        }else{
            message.error(response.msg)
        }
    }

    componentDidMount(){
        this.fetchSubmittedRequests('global-international')
    }

    render() {
        const title = 'Check Global International Mobility Proxy Forms';
        const extra = (<Button type="primary" icon={<PlusOutlined />} onClick={() => this.props.history.push('/dashboard/gi-submit')}>Add</Button>)

        return (
            <Card title={title} extra={extra}>
                <Table 
                    dataSource={this.state.submittedRequests} 
                    bordered 
                    pagination={{defaultPageSize:4, showQuickJumper: true, current:this.props.pageNum, onChange:(current)=>this.props.cachePageNum(current)}} 
                    loading={this.state.loading} 
                >
                    <Column 
                        title="File Name" 
                        dataIndex="fileName" 
                        key="fileName" 
                        render = {(fileName, record) => {
                            return <a href={record.url}>{fileName}</a>    
                        }}
                    />
                    <Column title="Requester" dataIndex="requester" key="requester" />
                    <Column title="Submit Date" dataIndex="submitDate" key="submitDate" />
                    <Column title="Comment" dataIndex="comment" key="comment" />
                    <Column
                        title="Status"
                        dataIndex="tags"
                        key="tags"
                        render={tags => (
                            <>
                                {tags.map(tag => {
                                    let color = "blue"
                                    return (
                                        <Tag color={color} key={tag}>
                                            {tag}
                                        </Tag>
                                    )
                                })}
                            </>
                        )}
                    />
                    <Column
                        title="Action"
                        key="action"
                        render={(text, record) => (
                            <Space size="middle">
                                <Link to={{pathname:"/dashboard/gi-request-detail",state:{...record}}}>Details</Link>
                            </Space>
                        )}
                    />
                </Table>
            </Card>
        )
    }
}

export default connect(
    state => ({pageNum:state.pageNum}),
    {cachePageNum}
)(withRouter(GICheck))
