import ajax from './ajax'

export const userAuth = () => {
    return ajax('/user/information',{})
           .then(response => {
             if(response.code === 0){
                return new Promise(resolve => {
                    resolve({ status: 0, msg: response.msg, data: { email: response.data.attributes.sub } })
                })
             }else{
                return new Promise(resolve => {
                    resolve({ status: response.code, msg: response.msg, data: {} })
                })
             }
           })
           .catch(error => {
                return new Promise(resolve => {
                    resolve({ status: 1, msg: error, data: {} })
                })
           })
}

