import React, { Component } from 'react'
import { connect } from 'react-redux'
import './LeftNav.less'
import Logo from '../../assets/images/logo.png'
import { Link } from 'react-router-dom'
import { Menu } from 'antd';
import menuList from '../../config/menuListConfig'

const { SubMenu } = Menu;

class LeftNav extends Component {

    state = {
        collapsed: false,
    };

    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    };

    generateMenuList = (menuList) => {
        return menuList.map(menu => {
            if (!menu.children) {
                return (
                    <Menu.Item key={menu.key} icon={menu.icon}>
                        <Link to={menu.key}>{menu.title}</Link>
                    </Menu.Item>
                )
            } else {
                return (
                    <SubMenu key={menu.key} icon={menu.icon} title={menu.title}>
                        {
                            menu.children.map(child => {
                                return (
                                    <Menu.Item key={child.key} icon={child.icon}>
                                        <Link to={child.key}>{child.title}</Link>
                                    </Menu.Item>
                                )
                            })
                        }
                    </SubMenu>
                )
            }
        })
    }

    render() {
        return (
            <div className='left-nav'>
                <Link to="/dashboard" className='left-nav-header'>
                    <img src={Logo} alt="logo"></img>
                </Link>
                <div style={{ width: 200 }}>
                    <Menu
                        defaultSelectedKeys={[menuList[0].key]}
                        defaultOpenKeys={[]}
                        mode="inline"
                        theme="light"
                        inlineCollapsed={this.state.collapsed}
                    >
                        {
                            this.generateMenuList(menuList)
                        }
                    </Menu>
                </div>
            </div>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(LeftNav)
