import React, { Component } from 'react'
import { connect } from 'react-redux'
import Home from '../Home/Home'
import { Redirect, Switch, Route } from 'react-router-dom'
import { Layout } from 'antd';
import RequestSubmit from './RequestSubmit'
import RequestCheck from './RequestCheck'
import RequestStatistics from './RequestStatistics'
import RequestDetails from './RequestDetails'

const { Content } = Layout;

class DashboardContent extends Component {
    render() {
        return (
            <Content className="site-layout-background" style={{ padding: 24, margin: 0, minHeight: 280}}>
                <Switch>
                    <Route path="/dashboard/home" component={Home}></Route>
                    <Route path="/dashboard/gi-submit" component={RequestSubmit}></Route>
                    <Route path="/dashboard/gi-check" render={(props)=><RequestCheck key='1' {...props}/>}></Route>
                    <Route path="/dashboard/do-submit" component={RequestSubmit}></Route>
                    <Route path="/dashboard/do-check" render={(props)=><RequestCheck key='2' {...props}/>}></Route>
                    <Route path="/dashboard/gi-statistics" component={RequestStatistics}></Route>
                    <Route path="/dashboard/do-statistics" component={RequestStatistics}></Route>
                    <Route path="/dashboard/do-request-detail" component={RequestDetails}></Route>
                    <Route path="/dashboard/gi-request-detail" component={RequestDetails}></Route>
                    <Redirect to="/dashboard/home" />
                </Switch>
            </Content>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(DashboardContent)
