import React, { Component } from 'react'
import { Card, DatePicker, Space } from 'antd'
import moment from 'moment';
import ReactEcharts from 'echarts-for-react'
import './Home.less'

const { RangePicker } = DatePicker;

export default class Home extends Component {

    getOptionForGrantTotal = () => {
        var option = {
            title: {
                text: 'Grant Total'
            },
            tooltip: {
                trigger: 'axis'
            },
            legend: {
                data: ['Global-Int', 'Domestic']
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            toolbox: {
                feature: {
                    saveAsImage: {}
                }
            },
            xAxis: {
                type: 'category',
                boundaryGap: false,
                data: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]
            },
            yAxis: {
                type: 'value'
            },
            series: [
                {
                    name: 'Global-Int',
                    type: 'line',
                    stack: 'Total',
                    data: [120, 132, 101, 134, 90, 230, 210, 120, 132, 101, 134, 90, 230, 210]
                },
                {
                    name: 'Domestic',
                    type: 'line',
                    stack: 'Total',
                    data: [220, 182, 191, 234, 290, 330, 310, 220, 182, 191, 234, 290, 330, 310]
                },
            ]
        };

        return option;
    }

    
    state = { requestTotal: { gi: 1000, do: 800 } }

    extra = (
        <Space direction="vertical" size={12}>
        <RangePicker
          defaultValue={[moment('2015/01/01', this.dateFormat), moment('2015/01/01', this.dateFormat)]}
          format={this.dateFormat}
        />
      </Space>
    )

    render() {
        return (
            <div className='home'>
                <div className="grantTotal">
                    <Card title='Proxy Forms Request Total' style={{ width: 250 }}>
                        <p>Global-Int: {this.state.requestTotal.gi}</p>
                        <p>Domestic: {this.state.requestTotal.do}</p>
                    </Card>
                    <ReactEcharts option={this.getOptionForGrantTotal()} />
                </div>
            </div>
        )
    }
}
