import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Button, Form, Upload, Input, Card, message } from 'antd'
import { UploadOutlined, CheckOutlined } from '@ant-design/icons';
import {submitRequests} from '../../api'

const formItemLayout = {
    labelCol: { span: 6 },
    wrapperCol: { span: 14 },
};

//handle the single file upload
const normFile = (e) => {
    console.log('Upload event:', e);
    let {file,fileList} = e;

    if(e.file.status === 'done'){
        message.success('File upload successful')
        //update the file name in oss server
        const {name,url} = e.file.response.data
        file = fileList[fileList.length-1]
        file.name = name
        file.url = url
    }else if(e.file.status === 'error'){
        message.error('File upload failed')
    }

    if (Array.isArray(e)) {
        return e;
    }
    return e && e.fileList;
};


//submit the form data to back end to process
const onFinish = async (values) => {
    console.log('Received values of form: ', values);
    let response = await submitRequests('global-international',values.upload,values.user.comments)
    if(response.status === 0){
        message.success(response.msg)
    }else{
        message.error(response.msg)
    }
};

class GISubmit extends Component {

    render() {
        const title = 'Submit Global International Mobility Proxy Forms';
        const extra = (<Button type="primary" icon={<CheckOutlined />} onClick={()=>this.props.history.push('/dashboard/gi-check')}>Check</Button>)

        return (
            <Card title={title} extra={extra}>
                <Form name="validate_other" {...formItemLayout} onFinish={onFinish}>
                {/* parameter name is identifying the file-name parm sent to back end */}
                    <Form.Item
                        name="upload"
                        label="Upload"
                        valuePropName="fileList"
                        getValueFromEvent={normFile}
                        extra="Upload proxy forms data"
                    >
                        {/* backend should upload the file to oss server and return the result below
                            {
                                "status": 0,
                                "data": {
                                    "name": "file-xxxxxxxx",
                                    "url": "http://xxxxxxxxx"
                                }
                            }
                        */}
                        <Upload name="logo" action="/global-int/upload" listType="text" accept=".csv, .xls, .xlsx">
                            <Button icon={<UploadOutlined />}>Click to upload</Button>
                        </Upload>
                    </Form.Item>

                    <Form.Item name={['user', 'comments']} label="Comments">
                        <Input.TextArea allowClear={true}/>
                    </Form.Item>

                    <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                        <Button type="primary" htmlType="submit">
                            Submit
                    </Button>
                    </Form.Item>
                </Form>
            </Card>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(GISubmit)
