import {connect} from 'react-redux'
import React, { PureComponent } from 'react'
import './Launch.less'
import Logo from './images/logo.png'
import { Button, message } from 'antd';
import {userAuth} from '../../api'
import {cacheUser} from '../../redux/actions/user'

class Launch extends PureComponent {

    launch = async () => {
        //axios to do authorization
        const response = await userAuth()
        if(response.status === 0){
            message.success('Authorization is successful')

            this.props.history.replace('/dashboard')
        } else {
            message.error(response.msg)
        }
    }

    render() {
        return (
            <div className='launch'>
                <header className='launch-header'>
                    <img src={Logo} alt='logo' />
                    <h1>React后台项目管理系统</h1>
                </header>
                <section className='launch-content'>
                    <span>Automate the creation of proxy forms for Mobility Domestic assignments for the US and Officers direct billed expenses which has a payment type of company paid. </span>
                    <div>
                        <Button onClick={this.launch} type="primary" size='large'>Launch</Button>
                    </div>
                </section>
            </div>
        )
    }
}

export default connect(
    state => ({user:state.user}),
    {
        cacheUser
    }
    )(Launch)
