import {CACHEUSER, UNCACHEUSER} from '../user_const'
import storageUtils from '../../utils/storageUtils'

const undefinedUser = storageUtils.getUser();

export default function countReducer(prevState=undefinedUser,action){
    const {type,data} = action;
    switch (type) {
        case CACHEUSER:
            storageUtils.saveUser(data);
            return data;
        case UNCACHEUSER:
            storageUtils.removeUser();
        default:
            return prevState;
    }
}
