import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Switch, Route } from 'react-router-dom'
import { Layout } from 'antd';
import './Dashboard.less'
import LeftNav from '../LeftNav/LeftNav'
import TopHeader from '../TopHeader/TopHeader'
import Home from '../Home/Home'

const { Header, Footer, Sider, Content } = Layout;

class Dashboard extends Component {
    render() {
        let { user } = this.props;

        if (Object.keys(user).length === 0) {
            return <Redirect to="/launch"></Redirect>
        }

        return (
            <Layout className='dashboard'>
                {/* <h2>Dashboard....{this.props.user.email}</h2> */}
                <Sider className='dashboard-sider' theme='light'>
                    <LeftNav />
                </Sider>
                <Layout>
                    <Header className='dashboard-header'>
                        <TopHeader />
                    </Header>
                    <Content className='dashboard-content' style={{ minHeight: document.documentElement.clientHeight - 135 }}>
                        <Switch>
                            <Route path="/dashboard/home" component={Home}></Route>
                            <Redirect to="/dashboard/home" />
                        </Switch>
                    </Content>
                    <Footer className='dashboard-footer'>&copy; Copyright 2021 - IBM CEPS - All Rights Reserved</Footer>
                </Layout>
            </Layout>
        )
    }
}

export default connect(state => ({ user: state.user }), {})(Dashboard)
