import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Switch, Route } from 'react-router-dom'
import './Dashboard.less'
import LeftNav from '../LeftNav/LeftNav'
import TopHeader from '../TopHeader/TopHeader'
import Home from '../Home/Home'
import GISubmit from '../GISubmit/GISubmit'
import GICheck from '../GICheck/GICheck'
import DOSubmit from '../DOSubmit/DOSubmit'
import DOCheck from '../DOCheck/DOCheck'
import Logo from '../../assets/images/logo.png'
import { Layout, Menu, Breadcrumb } from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined } from '@ant-design/icons';

const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

class Dashboard extends Component {
    render() {
        let { user } = this.props;

        if (Object.keys(user).length === 0) {
            return <Redirect to="/launch"></Redirect>
        }

        return (
            <Layout>
            <Sider>Sider</Sider>
            <Layout>
              <Header>Header</Header>
              <Content>Content</Content>
              <Footer>Footer</Footer>
            </Layout>
          </Layout>
        )
    }
}

export default connect(state => ({ user: state.user }), {})(Dashboard)
