import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { Button, Card, Table, Tag, Space, message } from 'antd'
import { PlusOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import {getSubmittedRequests} from '../../api'
import {Link} from 'react-router-dom'
const { Column } = Table;

class GICheck extends Component {
    state = {submittedRequests:[],loading:false, current:1}

    fetchSubmittedRequests = async (type) => {
        this.setState({loading:true})
        let response = await getSubmittedRequests(type)
        if(response.status === 0){
            let submittedRequests = response.data
            this.setState({submittedRequests,loading:false})
        }else{
            message.error("Fetch submitted requests failed")
        }
    }

    componentDidMount(){
        this.fetchSubmittedRequests('global-international')
    }

    render() {
        const title = 'Check Global International Mobility Proxy Forms';
        const extra = (<Button type="primary" icon={<PlusOutlined />} onClick={() => this.props.history.push('/dashboard/gi-submit')}>Add</Button>)
        return (
            <Card title={title} extra={extra}>
                <Table 
                    dataSource={this.state.submittedRequests} 
                    bordered 
                    pagination={{defaultPageSize:4, showQuickJumper: true}} 
                    loading={this.state.loading} 
                    current={this.state.current}
                    onChange={(page) => {console.log(page.current);this.setState(page.current)}}
                >
                    <Column 
                        title="File Name" 
                        dataIndex="fileName" 
                        key="fileName" 
                        render = {fileName => {
                            return <a href="javascript:;">{fileName}</a>    
                        }}
                    />
                    <Column title="Requestor" dataIndex="requestor" key="requestor" />
                    <Column title="Submit Date" dataIndex="submitDate" key="submitDate" />
                    <Column title="Comment" dataIndex="comment" key="comment" />
                    <Column
                        title="Status"
                        dataIndex="tags"
                        key="tags"
                        render={tags => (
                            <>
                                {tags.map(tag => {
                                    let color = "blue"
                                    return (
                                        <Tag color={color} key={tag}>
                                            {tag}
                                        </Tag>
                                    )
                                })}
                            </>
                        )}
                    />
                    <Column
                        title="Action"
                        key="action"
                        render={(text, record) => (
                            <Space size="middle">
                                <Link to={{pathname:"/dashboard/gi-request-detail",state:{...record}}}>Details</Link>
                            </Space>
                        )}
                    />
                </Table>
            </Card>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(withRouter(GICheck))
