import React, { Component } from 'react'
import { Card, DatePicker, Space } from 'antd'
import moment from 'moment';
import ReactEcharts from 'echarts-for-react'
import './Home.less'

const { RangePicker } = DatePicker;

const dateFormat = 'YYYY/MM/DD';

const tabList = [
    {
        key: 'globalInt',
        tab: 'Global-Int',
    },
    {
        key: 'domestic',
        tab: 'Domestic',
    },
];

const contentList = {
    globalInt: <p>content1</p>,
    domestic: <p>content2</p>,
};

export default class Home extends Component {

    state = {
        key: 'tab1',
        noTitleKey: 'app',
    };

    onTabChange = (key, type) => {
        console.log(key, type);
        this.setState({ [type]: key });
    };

    getOption = () => {
        var option = {
            title: {
                text: 'Grant Total'
            },
            tooltip: {
                trigger: 'axis'
            },
            legend: {
                data: ['Global-Int', 'Domestic']
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            toolbox: {
                feature: {
                    saveAsImage: {}
                }
            },
            xAxis: {
                type: 'category',
                boundaryGap: false,
                data: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]
            },
            yAxis: {
                type: 'value'
            },
            series: [
                {
                    name: 'Global-Int',
                    type: 'line',
                    stack: '总量',
                    data: [120, 132, 101, 134, 90, 230, 210, 120, 132, 101, 134, 90, 230, 210]
                },
                {
                    name: 'Domestic',
                    type: 'line',
                    stack: '总量',
                    data: [220, 182, 191, 234, 290, 330, 310, 220, 182, 191, 234, 290, 330, 310]
                },
            ]
        };

        return option;
    }

    state = { requestTotal: { gi: 1000, do: 800 } }

    extra = (
        <Space direction="vertical" size={12}>
        <RangePicker
          defaultValue={[moment('2015/01/01', dateFormat), moment('2015/01/01', dateFormat)]}
          format={dateFormat}
        />
      </Space>
    )
    render() {
        return (
            <div className='home'>
                <div className="grantTotal">
                    <Card title='Proxy Forms Request Total' style={{ width: 250 }}>
                        <p>Global-Int: {this.state.requestTotal.gi}</p>
                        <p>Domestic: {this.state.requestTotal.do}</p>
                    </Card>
                    <ReactEcharts option={this.getOption()} />
                </div>
                <div className="details">
                    <Card
                        style={{ width: '100%' }}
                        // title="Statistics"
                        extra={this.extra}
                        tabList={tabList}
                        activeTabKey={this.state.key}
                        onTabChange={key => {
                            this.onTabChange(key, 'key');
                        }}
                    >
                        {contentList[this.state.key]}
                    </Card>
                </div>
            </div>
        )
    }
}
