import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import './LeftNav.less'
import Logo from '../../assets/images/logo.png'
import { Link } from 'react-router-dom'
import { Menu } from 'antd';
import menuList from '../../config/menuListConfig'
import { withRouter } from 'react-router-dom'

const { SubMenu } = Menu;

class LeftNav extends PureComponent {

    constructor(props){
        super(props)
        this.path = this.props.location.pathname
        this.menuNodes = this.generateMenuList(menuList)
    }

    generateMenuList = (menuList) => {
        console.log('generateMenuList')
        return menuList.map(menu => {
            if (!menu.children) {
                return (
                    <Menu.Item key={menu.key} icon={menu.icon}>
                        <Link to={menu.key}>{menu.title}</Link>
                    </Menu.Item>
                )
            } else {
                //check if current item should be open
                const cItem = menu.children.find(item => item.key === this.path)
                if(cItem){
                    this.openKey = menu.key
                }
             
                return (
                    <SubMenu key={menu.key} icon={menu.icon} title={menu.title}>
                        {
                            this.generateMenuList(menu.children)
                        }
                    </SubMenu>
                )
            }
        })
    }

    render() {
        console.log('render')
        this.path = this.props.location.pathname

        return (
            <div className='left-nav'>
                <Link to="/dashboard" className='left-nav-header'>
                    <img src={Logo} alt="logo"></img>
                </Link>
                <div style={{ width: 200 }}>
                    <Menu
                        selectedKeys={[this.path]}
                        defaultOpenKeys={[this.openKey]}
                        mode="inline"
                        theme="light"
                    >
                        {
                            // this.menuNodes
                            this.generateMenuList(menuList)
                        }
                    </Menu>
                </div>
            </div>
        )
    }
}

export default connect(
    state => ({}),
    {}
)(withRouter(LeftNav))
