import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import { Layout } from 'antd';
import './Dashboard.less'
import LeftNav from '../LeftNav/LeftNav'
import TopHeader from '../TopHeader/TopHeader'

const { Header, Footer, Sider, Content } = Layout;

class Dashboard extends Component {
    render() {
        let { user } = this.props;

        if (Object.keys(user).length === 0) {
            return <Redirect to="/launch"></Redirect>
        }

        return (
            <div style={{height:'100%'}}>
                <Layout className='dashboard'>
                    {/* <h2>Dashboard....{this.props.user.email}</h2> */}
                    <Sider breakpoint="lg">
                        <LeftNav />
                    </Sider>
                    <Layout>
                        <Header>
                            <TopHeader />
                        </Header>
                        <Content className='dashboard-content'>Content</Content>
                        <Footer className='dashboard-footer'>&copy; Copyright 2021 - IBM CEPS - All Rights Reserved</Footer>
                    </Layout>
                </Layout>
            </div>
        )
    }
}

export default connect(state => ({ user: state.user }), {})(Dashboard)
