import React, { Component } from 'react'
import { Card, DatePicker, message } from 'antd'
import moment from 'moment';
import ReactEcharts from 'echarts-for-react'
import './Home.less'
import {getGiStatistics, getDoStatistics} from '../../api'

const { RangePicker } = DatePicker;

export default class Home extends Component {

    getOptionForGrantTotal = async () => {
        console.log('getOptionForGrantTotal')
        let giResponse = await getGiStatistics()
        let gistatistics = []
        let giTotal = 0
        if(giResponse.code === 0){
            gistatistics = giResponse.data.gistatistics
            giTotal = giResponse.data.giTotal
        }else{
            message.error("Fetch Global International Statistics failed")
        }

        let doResponse = await getDoStatistics()
        let dostatistics = []
        let doTotal = 0
        if(doResponse.code === 0){
            dostatistics = doResponse.data.dostatistics
            doTotal = doResponse.data.doTotal
        }else{
            message.error("Fetch Domestic Statistics failed")
        }

        var option = {
            title: {
                text: 'Grant Total'
            },
            tooltip: {
                trigger: 'axis'
            },
            legend: {
                data: ['Global-Int', 'Domestic']
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            toolbox: {
                feature: {
                    saveAsImage: {}
                }
            },
            xAxis: {
                type: 'category',
                boundaryGap: false,
                data: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"]
            },
            yAxis: {
                type: 'value'
            },
            series: [
                {
                    name: 'Global-Int',
                    type: 'line',
                    stack: 'Total',
                    data: gistatistics
                },
                {
                    name: 'Domestic',
                    type: 'line',
                    stack: 'Total',
                    data: dostatistics
                },
            ]
        };

        this.setState({ requestTotal: { gi: giTotal, do: doTotal } })

        return option;
    }

    
    state = { requestTotal: { gi: 0, do: 0 } }

    // extra = (
    //     <Space direction="vertical" size={12}>
    //     <RangePicker
    //       defaultValue={[moment('2015/01/01', this.dateFormat), moment('2015/01/01', this.dateFormat)]}
    //       format={this.dateFormat}
    //     />
    //   </Space>
    // )

    render() {
        return (
            <div className='home'>
                <div className="grantTotal">
                    <Card title='Proxy Forms Request Total' style={{ width: 250 }}>
                        <p>Global-Int: {this.state.requestTotal.gi}</p>
                        <p>Domestic: {this.state.requestTotal.do}</p>
                    </Card>
                    <ReactEcharts option={this.getOptionForGrantTotal()} />
                </div>
            </div>
        )
    }
}
