import { Button } from 'antd'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Card } from 'antd'
import {cachePageNum2} from '../../redux/actions/page'
import {getrequestDetails} from '../../api'

class GIReqDetail extends Component {
    state = {requestDetails:[],loading:false}

    fetchrequestDetails = async (key) => {
        this.setState({loading:true})
        let response = await getrequestDetails(key)
        if(response.code === 0){
            let requestDetails = response.data
            this.setState({requestDetails,loading:false})
        }else{
            message.error("Fetch request detail failed")
        }
    }

    componentDidMount(){
        let { key } = this.props.location.state
        this.fetchrequestDetails(key,'global-international')
    }
    
    render() {
        let { fileName } = this.props.location.state
        const title = `Globla International Proxy Forms Request Detail - ${fileName}`
        const extra = (
            <Button type='primary' onClick={() => { this.props.history.goBack() }}>Return</Button>
        )
        return (
            <div>
                <Card title={title} extra={extra}>
                    <Table
                        dataSource={this.state.requestDetails}
                        bordered
                        pagination={{ defaultPageSize: 4, showQuickJumper: true, current: this.props.pageNum, onChange: (current) => this.props.cachePageNum2(current) }}
                        loading={this.state.loading}
                    >
                        <Column title="Report ID" dataIndex="reportId" key="reportId"/>
                        <Column title="Expense Type" dataIndex="expenseType" key="expenseType" />
                        <Column title="TransactionDate" dataIndex="transactionDate" key="transactionDate" />
                        <Column title="Amount" dataIndex="expenseAmount" key="expenseAmount" />
                        <Column title="Result" dataIndex="result" key="result" />
                    </Table>
                </Card>
            </div>
        )
    }
}

export default connect(
    state => ({pageNum:state.pageNum2}),
    {cachePageNum2}
)(GIReqDetail)
