}

export const getGiStatistics = () => {
    // return ajax('/gistastics',{});
    return new Promise(resolve => {
        resolve({ status: 0, msg: 'GI Statics Load successfully', data: { gistatistics: [10,20,30,10,20,22,10,20,30,10,20,22] } })
    })
}

export const getDoStatistics = () => {
    // return ajax('/gistastics',{});
    return new Promise(resolve => {
        resolve({ status: 0, msg: 'GI Statics Load successfully', data: { dostatistics: [10,20,30,10,20,22,10,20,30,10,20,22] } })
    })
}

export const submitRequests = (type,filelist,comment) => {
    // return ajax('/submittedRequests',{type,filelist,comments},'POST')
    let fileName = filelist[0].name
    let requester = storageUtils.getUser()
    if(type === 'global-international'){
        return ajax('/global-int/submittedRequests',{fileName,requester,comment},'POST')
    }else if(type === 'domestic'){
        return ajax('/domestic/submittedRequests',{fileName,requester,comment},'POST')
    }
    // return new Promise(resolve => {
    //     resolve({ status: 0, msg: 'Request is submitted successfully', data: {} })
    // })
}

export const getSubmittedRequests = (type) => {
    if(type === 'global-international'){
        return ajax('/global-int/submittedRequests')
    }else if(type === 'domestic'){
        return ajax('/domestic/submittedRequests')
    }
}
