import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import './Dashboard.less'
import DashboardSider from '../DashboardSider/DashboardSider'
import DashboardHeader from '../DashboardHeader/DashboardHeader'

import { Layout, Menu, Breadcrumb, Button, Tooltip, Input, Space  } from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined, SearchOutlined, AudioOutlined  } from '@ant-design/icons';
import DashboardContent from '../DashboardContent/DashboardContent'

const { SubMenu } = Menu;
const { Header, Content, Sider, Footer } = Layout;

const { Search } = Input;

const suffix = (
  <AudioOutlined
    style={{
      fontSize: 16,
      color: '#1890ff',
    }}
  />
);

class Dashboard extends Component {
    render() {
        let { user } = this.props;

        if (Object.keys(user).length === 0) {
            return <Redirect to="/launch"></Redirect>
        }

        return (
            <Layout>
                <DashboardHeader/>
                <Layout>
                    <DashboardSider/>
                    <Layout style={{ padding: '0 24px 24px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>Home</Breadcrumb.Item>
                            <Breadcrumb.Item>List</Breadcrumb.Item>
                            <Breadcrumb.Item>App</Breadcrumb.Item>
                        </Breadcrumb>
                        <DashboardContent/>
                        <Footer className='dashboard-footer'>&copy; Copyright 2021 - IBM CEPS - All Rights Reserved</Footer>
                    </Layout>
                </Layout>
            </Layout>
        )
    }
}

export default connect(state => ({ user: state.user }), {})(Dashboard)
