import React, { Component } from 'react'
import { connect } from 'react-redux'
import './DashboardHeader.less'
import {unCacheUser} from '../../redux/actions/user'
import Logo from '../../assets/images/logo.png'
import { withRouter } from 'react-router-dom'
import { Layout, Button, Input, message } from 'antd';

const { Header } = Layout;
const { Search } = Input;
const onSearch = value => console.log(value);

class DashboardHeader extends Component {

    logoff = () => {
        message.success('Logoff successfully')
        //clear the cached user in store
        this.props.unCacheUser()
        //Route to launch
        this.props.history.replace('/launch')
    }

    render() {
        return (
            <Header className="dashboard-header">
                <div className="logo" >
                    <img src={Logo} alt='logo'></img>
                </div>
                <div className="user-info">
                    <span>Welcome</span>
                </div>
                <div className="logoff">
                    <Search placeholder="input search text" onSearch={onSearch} style={{ width: 200 }} />
                    {/* <Search placeholder="input search text" onSearch={onSearch} enterButton /> */}
                    {/* <Button type="link" onClick={this.logoff}>Logoff</Button> */}
                    <Button onClick={showConfirm}>Logoff</Button>
                </div>
            </Header>
        )
    }
}

export default connect(
    state => ({ user: state.user }),
    {unCacheUser}
)(withRouter(DashboardHeader))
