import React, { Component } from 'react'
import { connect } from 'react-redux'
import './DashboardHeader.less'
import Logo from '../../assets/images/logo.png'
import { Layout, Menu, Breadcrumb, Button, Tooltip, Input, Space  } from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined, SearchOutlined, AudioOutlined  } from '@ant-design/icons';

const { SubMenu } = Menu;
const { Header, Content, Sider, Footer } = Layout;

const { Search } = Input;

const suffix = (
  <AudioOutlined
    style={{
      fontSize: 16,
      color: '#1890ff',
    }}
  />
);

const onSearch = value => console.log(value);

class TopHeader extends Component {
    render() {
        return (
            <Header className="dashboard-header">
                <div className="logo" >
                    <img src={Logo} alt='logo'></img>
                </div>
                <div className="user-info">
                    <span>Welcome</span>
                </div>
                <div className="logoff">
                    <Search placeholder="input search text" onSearch={onSearch} style={{ width: 200 }} />
                    {/* <Search placeholder="input search text" onSearch={onSearch} enterButton /> */}
                    <Button type="link">Logoff</Button>
                </div>
            </Header>
        )
    }
}

export default connect(
    state => ({ user: state.user }),
    {}
)(TopHeader)
